INSERT INTO public.report_templates VALUES ('6328a3f5-88db-47f7-b3ee-9a08e9bc95d8', 'Condensation Survey Report v1', 'condensation', 1, '[{"key": "cover", "type": "cover", "order": 1, "title": "Report", "required": true, "data_fields": ["client_name", "site_address", "site_address_line2", "site_city", "site_county", "site_postcode", "internal_reference", "inspection_date", "weather_conditions", "temperature_c"], "content_source": "survey_data"}, {"key": "property", "type": "property", "order": 2, "title": "The Property", "required": true, "data_fields": ["property_type", "construction_type", "approx_build_year"], "content_source": "mixed", "photo_categories": ["property_front", "building_exterior", "street_view"]}, {"key": "survey_context", "type": "boilerplate", "order": 3, "title": "The Survey/Specific Defects Inspection", "required": true, "sub_sections": [{"key": "general_notes", "type": "boilerplate", "order": 1, "title": "", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "Our findings and proposals are set out below and should be read in conjunction with the enclosed document ''General Notes for clients and Health and Safety precautions''.", "condition": "always"}]}, {"key": "orientation", "type": "boilerplate", "order": 2, "title": "Orientation", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "The terms left, right, front and rear are used in accordance with facing the front elevation from the outside of the building.", "condition": "always"}]}, {"key": "scope", "type": "boilerplate", "order": 3, "title": "The Scope", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "Our specific defects inspection was solely to identify evidence of problems that you have requested us to inspect which was that of mould and condensation within the above property.", "condition": "always"}]}, {"key": "abbreviations", "type": "boilerplate", "order": 4, "title": "Abbreviations Used In Report", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "PIV = Positive Input Ventilation, ACMs = Asbestos containing materials.", "condition": "always"}]}], "content_source": "template", "boilerplate_variants": [{"text": "In accordance with your specific instructions, we carried out a survey of the above property for problems with mould, mildew & condensation.", "condition": "always"}]}, {"key": "internal_inspection", "type": "findings", "order": 4, "title": "Internal Inspection", "required": true, "data_fields": ["humidity_reading"], "repeats_per": "room", "content_source": "mixed", "llm_prompt_hint": "Describe the condensation and mould findings for each room inspected. Reference humidity readings, window condensation, mould severity, and ventilation observations. Include inline photo references.", "photo_categories": ["condensation_evidence", "room_overview"], "boilerplate_variants": [{"text": "During the internal inspection the following issues were noted:", "condition": "always"}, {"text": "Condensation, was apparent on the windows.", "condition": "condensation_on_windows"}, {"text": "Areas of low temperature/poor air movement to the walls creating dew points.", "condition": "low_temp_dew_points"}, {"text": "Black spot mould was present within the building.", "condition": "black_mould"}, {"text": "There was a lack of ventilation and air movement within the property.", "condition": "lack_ventilation"}, {"text": "We suspect poor insulation values due to solid brick walls.", "condition": "poor_insulation"}, {"text": "Humidity readings were taken within the property, these readings peaked at", "condition": "humidity_reading"}]}, {"key": "causes", "type": "findings", "order": 5, "title": "Causes", "required": true, "content_source": "mixed", "llm_prompt_hint": "Explain the root causes of the condensation problems based on the survey findings. Reference lack of ventilation, poor insulation, humidity levels, and any contributing factors.", "boilerplate_variants": [{"text": "The above problems have been caused by:", "condition": "always"}, {"text": "A lack of ventilation and air movement within the property.", "condition": "cause_ventilation"}, {"text": "Poor insulation values of solid brick walls.", "condition": "cause_insulation"}, {"text": "Dampness within the fabric of the building.", "condition": "cause_dampness"}, {"text": "Lack of ventilation and air movement with the property have caused elevated humidity levels within the building.", "condition": "cause_elevated_humidity"}, {"text": "It is our opinion that the current extraction and ventilation at the property does not provide sufficient ventilation", "condition": "cause_insufficient_extraction"}]}, {"key": "proposals", "type": "proposals", "order": 6, "title": "Proposal", "required": true, "data_fields": ["piv_type", "piv_count", "fan_count"], "content_source": "mixed", "boilerplate_variants": [{"text": "We would propose for the following works to be carried out:", "condition": "always"}, {"text": "Supply and install a system of devices and measures to introduce fresh air and remove stale damp air. This system will reduce humidity levels and create air movement to combat the condensation problem within the property.", "condition": "ventilation_system"}, {"text": "You will need the following services:", "condition": "services_header"}, {"text": "Supply & install of loft mounted Dryair PIV heated unit(s) to the following area(s):", "condition": "piv_loft_heated"}, {"text": "Loft area and diffusor to hallway ceiling", "condition": "piv_loft_diffusor"}, {"text": "Supply & install of loft mounted Dryair PIV unheated unit(s) to the following area(s):", "condition": "piv_loft_unheated"}, {"text": "Supply & install of wall mounted Dryair PIV heated unit(s) to the following area(s):", "condition": "piv_wall_heated"}, {"text": "Supply & install of Humidistat trickle and boost fan(s) to the following area(s):", "condition": "fans_humidistat"}, {"text": "Upgrade the existing extractors at the property", "condition": "fans_upgrade"}, {"text": "Supply and installation of any necessary electrical fittings/ducting/trunking/grilles required for the installation of Fans/PIVs/Vents (if applicable).", "condition": "electrical_fittings"}, {"text": "Supply of labour and materials to treat and kill black mould (Any area treated for mould may require you to redecorate).", "condition": "mould_treatment"}, {"text": "Asbestos testing: The ceiling/areas designated for the above installations is covered in Artex/decorative plasterwork. These applications can be Asbestos containing materials (ACMs). It will therefore be necessary to have these tested prior to works commencing.", "condition": "asbestos_test"}, {"text": "Electrical system not up to current standards: A check of your electrical system revealed that it was not up to current standards. We recommend that you contact a qualified electrician to upgrade your system.", "condition": "electrical_not_current"}]}, {"key": "loft_access", "type": "findings", "order": 7, "title": "Loft Access", "required": false, "content_source": "mixed", "photo_categories": ["general"], "boilerplate_variants": [{"text": "Supply and install of new loft hatch with sturdy fold down ladder, handrail and switched lamp.", "condition": "new_loft_hatch"}, {"text": "Supply of labour and materials to enlarge current loft hatch.", "condition": "enlarge_loft_hatch"}]}, {"key": "piv_info", "type": "boilerplate", "order": 8, "title": "Additional Information: Benefits of PIV systems", "required": false, "content_source": "template", "boilerplate_variants": [{"text": "For more information on the benefits of Positive Input Ventilation systems please visit the link below:\nhttps://www.tyneteesdampproofing.co.uk/piv-video/", "condition": "piv_selected"}]}, {"key": "extent_of_survey", "type": "closing", "order": 9, "title": "Extent Of Survey", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "You should be aware that we have reported upon condensation problems evident to us at the time of our inspection and in accordance with your instructions. If there are any omissions or if you believe that we have misinterpreted your survey instruction, please let us know at once. Our recommendations will be for improvements that will help to combat the problems reported and visible to our surveyor at the time of inspection. Please read carefully the content of this report and all of its enclosures.", "condition": "always"}]}, {"key": "external_inspection", "type": "findings", "order": 10, "title": "EXTERNAL INSPECTION", "required": true, "data_fields": ["building_defects_found", "building_defects"], "sub_sections": [{"key": "building_defects", "type": "findings", "order": 1, "title": "Building Defects", "required": true, "content_source": "mixed", "boilerplate_variants": [{"text": "See Damp report for building defects.", "condition": "see_damp_report"}, {"text": "No building defects were noted at the time of our inspection.", "condition": "no_defects"}, {"text": "We noted the following building defects:", "condition": "defects_found"}, {"text": "The above items should be checked and addressed in the short term (3 months maximum) as they have been identified as possible causes of moisture ingress to the fabric of the building, failure to correct them may exacerbate the reported problems.", "condition": "defects_warning"}]}], "content_source": "mixed", "photo_categories": ["building_defect"]}, {"key": "surveyor_comments", "type": "findings", "order": 11, "title": "Additional Supporting Comments From Surveyor", "required": false, "content_source": "llm_generated", "llm_prompt_hint": "Provide practical advice about managing condensation. Mention repainting tips (stain blocker for ceilings), ventilation habits, and any additional observations. Keep tone professional but accessible."}, {"key": "surveyor_signature", "type": "signature", "order": 12, "title": "Report Author", "required": true, "data_fields": ["surveyor_name", "surveyor_credentials", "surveyor_title"], "content_source": "survey_data", "boilerplate_variants": [{"text": "Report produced under the guidance of Tyne Tees Damp Proofing Ltd by:", "condition": "always"}]}]', '{"company_name": "Tyne Tees Damp Proofing Ltd", "piv_info_url": "https://www.tyneteesdampproofing.co.uk/piv-video/", "title_prefix": "Specific Defects Inspection - Condensation", "abbreviations": {"PIV": "Positive Input Ventilation", "ACMs": "Asbestos containing materials"}, "company_website": "https://www.tyneteesdampproofing.co.uk", "guarantee_years": 25, "include_sketch_plan": false, "include_payment_terms": false, "general_notes_document": "General Notes for clients and Health and Safety precautions", "payment_deposit_percentage": 30}', true, '2026-02-20 02:08:46.547567+00', '2026-02-20 02:08:46.547567+00');
INSERT INTO public.report_templates VALUES ('7d215ff7-e2e5-4800-99a8-b0800cbad51a', 'Timber Survey Report v1', 'timber', 1, '[{"key": "cover", "type": "cover", "order": 1, "title": "Report", "required": true, "data_fields": ["client_name", "site_address", "site_address_line2", "site_city", "site_county", "site_postcode", "internal_reference", "inspection_date", "weather_conditions", "temperature_c"], "content_source": "survey_data"}, {"key": "property", "type": "property", "order": 2, "title": "The Property", "required": true, "data_fields": ["property_type", "construction_type", "approx_build_year"], "content_source": "mixed", "photo_categories": ["property_front", "building_exterior"]}, {"key": "survey_context", "type": "boilerplate", "order": 3, "title": "The Survey/Specific Defects Inspection", "required": true, "data_fields": ["reported_defect"], "sub_sections": [{"key": "general_notes", "type": "boilerplate", "order": 1, "title": "", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "Our findings and proposals are set out below and should be read in conjunction with the enclosed document ''General Notes for clients and Health and Safety precautions''.", "condition": "always"}]}, {"key": "orientation", "type": "boilerplate", "order": 2, "title": "Orientation", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "The terms left, right, front and rear are used in accordance with facing the front elevation from the outside of the building.", "condition": "always"}]}, {"key": "scope", "type": "boilerplate", "order": 3, "title": "The Scope", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "We must draw to your attention to the scope of our inspection. The inspection was solely to identify evidence of problems which were within those areas pointed out to us at the time of our visit which are listed above. Any areas not specifically referred to in this report have not been included and are not covered by the report or estimate.", "condition": "always"}]}, {"key": "abbreviations", "type": "boilerplate", "order": 4, "title": "Abbreviations Used In Report", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "W/W = Water Weight, SOP = Standard Operating Procedures.", "condition": "always"}]}], "content_source": "mixed", "boilerplate_variants": [{"text": "In accordance with your instructions, we carried out a survey/specific defects inspection to the above property for the reported problem which was:", "condition": "always"}]}, {"key": "external_building_defects", "type": "findings", "order": 4, "title": "External Building Defects", "required": true, "data_fields": ["building_defects_found", "building_defects"], "content_source": "mixed", "photo_categories": ["building_defect"], "boilerplate_variants": [{"text": "No building defects were noted at the time of our inspection.", "condition": "no_defects"}, {"text": "We noted the following building defects:", "condition": "defects_found"}, {"text": "The above items should be checked and addressed in the short term (3 months maximum) as they have been identified as possible causes of moisture ingress to the fabric of the building, failure to correct them may exacerbate the reported problems.", "condition": "defects_warning"}, {"text": "You should check your lease and find who is responsible for the upkeep of the roof, it is possible the upstairs flat may need to carry out the repairs or it could be shared cost. Whilst the roof repairs may fall under the freeholder''s responsibility, any damp or timber decay resulting from the roof failure will be your responsibility to remedy.", "condition": "flat_lease_warning"}]}, {"key": "ground_levels", "type": "findings", "order": 5, "title": "External Ground Levels", "required": false, "content_source": "mixed", "boilerplate_variants": [{"text": "There were no apparent ground level issues.", "condition": "no_ground_issues"}, {"text": "We noted high ground levels to the following areas:", "condition": "high_ground_levels"}, {"text": "External ground was falling towards building at:", "condition": "ground_falling_towards"}, {"text": "All ground levels to be maintained at a minimum of 150mm below DPC level, paths, drives, yards etc should not cause rainwater etc to run against your property walls. You should arrange work to correct this as a matter of urgency. Ideally, ground levels should be sloping away from the building to shed rainwater and to allow the building to dry out.", "condition": "ground_recommendation"}, {"text": "The rear yard would benefit from the installation of an aco drain channel to carry water to the drain.", "condition": "aco_drain_recommended"}]}, {"key": "sub_floor_ventilation", "type": "findings", "order": 6, "title": "Sub Floor Ventilation", "required": true, "data_fields": ["airbrick_clean_count", "airbrick_upgrade_count", "airbrick_new_count"], "content_source": "mixed", "boilerplate_variants": [{"text": "There were no airbricks installed.", "condition": "no_airbricks"}, {"text": "It was not possible to inspect the airbricks due to the following:", "condition": "airbricks_not_inspectable"}, {"text": "The sub floor voids were ventilated by the following ''visible'' number of airbricks:", "condition": "airbricks_present"}, {"text": "Remove air bricks, check airflow, adjust as necessary, clean debris & reinstall.", "condition": "proposal_remove_clean"}, {"text": "Install new 225 x 150 additional airbricks.", "condition": "proposal_install_new"}, {"text": "This will increase the airflow through the floor voids, reduce the humidity and the moisture content of linked timbers, which will greatly reduce the chances of attack by wood rotting fungi such as dry rot (serpulae lacrymans) in the future.", "condition": "proposal_benefit"}, {"text": "Lift the existing air bricks.", "condition": "proposal_lift"}, {"text": "Lifting the airbricks will help to prevent water ponding and snow melting, causing water to run into the sub floor void, elevating the moisture levels of the timbers within.", "condition": "lift_benefit"}, {"text": "No further works required.", "condition": "no_works_required"}]}, {"key": "room_inspection", "type": "findings", "order": 7, "title": "Internal Room Inspection Details", "required": true, "data_fields": ["room_name", "room_designation", "moisture_reading"], "repeats_per": "room", "sub_sections": [{"key": "ceiling", "type": "findings", "order": 1, "title": "Ceiling", "required": true, "content_source": "mixed", "boilerplate_variants": [{"text": "At the time of inspection there were no defects to the ceilings of this room.", "condition": "no_defects"}, {"text": "At the time of inspection the following defects were observed to the ceiling of this room:", "condition": "defects_found"}]}, {"key": "walls", "type": "findings", "order": 2, "title": "Walls", "required": true, "content_source": "mixed", "boilerplate_variants": [{"text": "At the time of inspection there were no defects to the walls of this room.", "condition": "no_defects"}, {"text": "At the time of inspection the following defects were observed to the walls of this room:", "condition": "defects_found"}]}, {"key": "floors_voids", "type": "findings", "order": 3, "title": "Floors and floor voids", "required": true, "content_source": "mixed", "boilerplate_variants": [{"text": "At the time of inspection there were no defects to the floor voids of this room.", "condition": "no_defects"}, {"text": "At the time of inspection the following defects were observed to the floor voids of this room:", "condition": "defects_found"}]}, {"key": "moisture_reading", "type": "data", "order": 4, "title": "Moisture Reading", "required": false, "content_source": "survey_data", "boilerplate_variants": [{"text": "Moisture reading taken from the timbers was recorded as:", "condition": "reading_taken"}]}], "content_source": "mixed", "llm_prompt_hint": "For each room, describe: room name and designation, ceiling condition, wall condition (joinery timbers, woodworm, wood rot, dampness), floor void condition (woodworm, wood rot, woodboring insects), and moisture reading. Reference photos inline.", "photo_categories": ["timber_damage", "room_overview"]}, {"key": "summary", "type": "findings", "order": 8, "title": "Summary", "required": true, "content_source": "mixed", "llm_prompt_hint": "Aggregate all room findings into a concise summary. List the timber issues found and their locations.", "boilerplate_variants": [{"text": "The timbers were suffering from the following issues:", "condition": "issues_intro"}, {"text": "A woodworm infestation, the attack was of common furniture beetle (anobium punctatum)", "condition": "issue_woodworm"}, {"text": "An attack of wood rotting fungi, this was identified as wet rot", "condition": "issue_wet_rot"}, {"text": "Rotting timbers were also suffering from an infestation of woodboring weevil (Pentarthrum Huttoni)", "condition": "issue_woodboring_weevil"}, {"text": "An attack of wood rotting fungi, this was identified as dry rot (sepulae lacrymans)", "condition": "issue_dry_rot"}, {"text": "A high W/W moisture level to the floor timbers, this was due to a lack of ventilation to the sub floor voids", "condition": "issue_high_moisture"}]}, {"key": "dry_rot_party_wall", "type": "boilerplate", "order": 9, "title": "Dry Rot - Treatment And Party Wall Act", "required": false, "content_source": "template", "boilerplate_variants": [{"text": "Should an attack of dry rot affect the party wall (the wall between you and your neighbouring property) it is important that we treat the fungal attack in its entirety, that is from both sides. The homeowner is required by law to give notice to their neighbour before carrying out work that affects a shared wall.", "condition": "dry_rot_party_wall"}, {"text": "Please find attached letter of notification to print out and pass to your neighbour, this should also assist with your party wall obligations (you are legally required to tell your neighbour of anything that may affect the party wall).", "condition": "party_wall_letter"}, {"text": "Non-guaranteed work: The work in this quotation is carried out as per your instructions and not as per our recommendations, we have no way of knowing if the attack has spread to adjoining properties. The guarantee will not apply to the party wall treatment.", "condition": "non_guaranteed_party_wall"}]}, {"key": "proposals", "type": "proposals", "order": 10, "title": "Proposals", "required": true, "content_source": "mixed", "boilerplate_variants": [{"text": "We would propose for the following works to be carried out:", "condition": "always"}, {"text": "Treatment for Dry Rot attack, as per the schedule specified below.", "condition": "proposal_dry_rot"}, {"text": "Timber replacement and treatment for wood rot, as per the schedule specified below.", "condition": "proposal_wet_rot"}, {"text": "Timber replacement and treatment for wood rot and woodboring insects, as per the schedule specified below.", "condition": "proposal_wet_rot_weevil"}, {"text": "Treatment for woodworm infestation, as per the schedule specified below.", "condition": "proposal_woodworm"}, {"text": "Obtain permission from the vendor to carry out a destructive survey. For your convenience we have included a pre-filled email template to request permission from the property owner.", "condition": "proposal_destructive_survey"}]}, {"key": "treatment_schedule", "type": "treatment", "order": 11, "title": "Treatment Schedule", "required": false, "sub_sections": [{"key": "schedule_woodworm", "type": "treatment", "order": 1, "title": "Schedule For Treatment Of Woodworm", "required": false, "content_source": "template", "boilerplate_variants": [{"text": "Create openings/lift floorboards as required ensuring no nails left sticking out of removed boards.\nCover open electric outlets with polythene to prevent wetting (electric shock hazard).\nApply fog to voids/surfaces as per SOP.\nMake good to all areas treated.", "condition": "woodworm_treatment"}]}, {"key": "schedule_dry_rot", "type": "treatment", "order": 2, "title": "Schedule For Treatment Of Dry Rot", "required": false, "content_source": "template", "boilerplate_variants": [{"text": "Open up affected area.\nSupport any structural bearing timbers for duration of works.\nCut out affected timbers as per SOP.\nTreat all remaining timber cut ends as per SOP.\nPrepare and place new timbers, do not fix.\nRemove new timbers.\nTreat all masonry as per SOP.\nClean out and sterilise joist wall socket holes as per SOP.\nApply fungal treatment gel to walls.\nInstall membrane system to protect walls and promote drying of masonry.\nTreat all surfaces of new timbers.\nInstall bower beam metal plating system.\nTreat and endwrap joists as per SOP.\nInstall and fix new timbers as per SOP.\nBag up and remove debris to safe external area.\nClean and tidy site.", "condition": "dry_rot_treatment"}]}, {"key": "schedule_wet_rot", "type": "treatment", "order": 3, "title": "Schedule For Treatment Of Wet Rot", "required": false, "content_source": "template", "boilerplate_variants": [{"text": "Open up affected area.\nCut out affected timbers as per SOP.\nTreat all remaining timber cut ends as per SOP.\nTreat all masonry as per SOP.\nClean out and sterilise joist wall socket holes as per SOP.\nTreat and endwrap joists as per SOP.\nInstall and fix new timbers as per SOP.\nBag up and remove debris to safe external area.\nClean and tidy site.", "condition": "wet_rot_treatment"}]}, {"key": "schedule_wet_rot_weevil", "type": "treatment", "order": 4, "title": "Schedule For Treatment Of Wet Rot And Woodboring Weevil", "required": false, "content_source": "template", "boilerplate_variants": [{"text": "Open up affected area.\nCut out affected timbers as per SOP.\nTreat all remaining timber cut ends as per SOP.\nTreat all masonry as per SOP.\nClean out and sterilise joist wall socket holes as per SOP.\nTreat all surfaces of new timbers with dual purpose application.\nTreat and endwrap joists as per SOP.\nInstall and fix new timbers as per SOP.\nBag up and remove debris to safe external area.\nClean and tidy site.", "condition": "wet_rot_weevil_treatment"}]}], "content_source": "template"}, {"key": "surveyor_comments", "type": "findings", "order": 12, "title": "Additional Supporting Comments From Surveyor", "required": false, "content_source": "llm_generated", "llm_prompt_hint": "Provide practical advice about the timber issues found. Mention guarantee details (25 year), recommend full solutions over patching, and note any safety concerns. Reference specific room findings."}, {"key": "ancillary_items", "type": "closing", "order": 13, "title": "Ancillary Items", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "No allowance has been made in our estimate for the removal or replacement of floor coverings, furnishings, furniture, roof void stored contents and any other items that may obstruct our work. Whilst we will be happy to roll back and loosely relay carpets, you should arrange for rooms to be cleared of anything delicate or of value, prior to our arrival on site. Unless otherwise agreed in writing, any time spent clearing areas to enable work to proceed will be charged for at our standard hourly day rates and added to your final account.", "condition": "always"}]}, {"key": "extent_of_survey", "type": "closing", "order": 14, "title": "Extent of Survey", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "The areas we have reported upon are those inspected in accordance with your instructions. If there are any omissions or if you believe that we have misinterpreted your survey instruction, please let us know at once. Where treatment has been recommended, unless otherwise stated above, this is on the understanding that the specified area has not previously been treated and guaranteed.\n\nYou should be aware that we have reported upon problems evident to us at the time of our inspection. We are not commenting in any general sense on the risks of dampness, fungal decay or any other defect not evident at the time of inspection, or that may develop in the future.", "condition": "always"}]}, {"key": "payment_terms", "type": "closing", "order": 15, "title": "Payment", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "An initial payment of 30% of the contract value will be payable in advance, this will give us confidence to order job specific materials, and to book the time for your project into our technicians schedules. The balance should always be paid in full within 7 days of completion.", "condition": "always"}]}, {"key": "surveyor_signature", "type": "signature", "order": 16, "title": "Report Author", "required": true, "data_fields": ["surveyor_name", "surveyor_credentials", "surveyor_title"], "content_source": "survey_data", "boilerplate_variants": [{"text": "Report produced under the guidance of Tyne Tees Damp Proofing Ltd by:", "condition": "always"}]}, {"key": "sketch_plan", "type": "sketch", "order": 17, "title": "Sketch Plan", "required": false, "content_source": "surveyor_input", "photo_categories": ["sketch_plan"]}]', '{"company_name": "Tyne Tees Damp Proofing Ltd", "title_prefix": "Timbers Inspection", "abbreviations": {"SOP": "Standard Operating Procedures", "W/W": "Water Weight"}, "company_website": "https://www.tyneteesdampproofing.co.uk", "guarantee_years": 25, "include_sketch_plan": true, "include_payment_terms": true, "general_notes_document": "General Notes for clients and Health and Safety precautions", "payment_deposit_percentage": 30}', true, '2026-02-20 02:08:46.550329+00', '2026-02-20 02:08:46.550329+00');
INSERT INTO public.report_templates VALUES ('7a9411f3-b962-4310-bbce-1fcb088263c5', 'Woodworm Survey Report v1', 'woodworm', 1, '[{"key": "cover", "type": "cover", "order": 1, "title": "Report", "required": true, "data_fields": ["client_name", "site_address", "site_address_line2", "site_city", "site_county", "site_postcode", "internal_reference", "inspection_date", "weather_conditions", "temperature_c"], "content_source": "survey_data"}, {"key": "property", "type": "property", "order": 2, "title": "The Property", "required": true, "data_fields": ["property_type", "construction_type", "approx_build_year"], "content_source": "mixed", "photo_categories": ["property_front", "building_exterior"]}, {"key": "reported_problem", "type": "data", "order": 3, "title": "Reported Problem", "required": true, "data_fields": ["reported_defect"], "content_source": "survey_data", "boilerplate_variants": [{"text": "Woodworm suspected to:", "condition": "always"}]}, {"key": "scope", "type": "boilerplate", "order": 4, "title": "The Scope", "required": true, "sub_sections": [{"key": "general_notes", "type": "boilerplate", "order": 1, "title": "", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "Our findings and proposals are set out below and should be read in conjunction with the enclosed document ''General Notes for clients and Health and Safety precautions''.", "condition": "always"}]}], "content_source": "template", "boilerplate_variants": [{"text": "We must draw to your attention to the scope of our inspection. The inspection was solely to identify evidence of problems which were within those areas pointed out to us at the time of our visit which are listed above. Any areas not specifically referred to in this report have not been included and are not covered by the report or estimate.", "condition": "always"}]}, {"key": "inspection_findings", "type": "findings", "order": 5, "title": "Inspection", "required": true, "data_fields": ["species_identified", "infestation_status"], "sub_sections": [{"key": "ground_floor_rooms", "type": "findings", "order": 1, "title": "Ground floor rooms:", "required": false, "repeats_per": "room", "content_source": "survey_data"}, {"key": "first_floor_rooms", "type": "findings", "order": 2, "title": "First floor rooms:", "required": false, "repeats_per": "room", "content_source": "survey_data"}, {"key": "loft_rooms", "type": "findings", "order": 3, "title": "Loft area/Attic rooms:", "required": false, "repeats_per": "room", "content_source": "survey_data"}, {"key": "other_rooms", "type": "findings", "order": 4, "title": "Other rooms:", "required": false, "repeats_per": "room", "content_source": "survey_data"}, {"key": "inaccessible_areas", "type": "findings", "order": 5, "title": "Inaccessible Areas", "required": false, "content_source": "mixed", "boilerplate_variants": [{"text": "It was not possible to inspect all timbers at the time of our visit. Some areas were inaccessible because of:", "condition": "inaccessible_intro"}, {"text": "Laminate/Wood overlay.", "condition": "obstruction_laminate"}, {"text": "Tacked or specialist carpet.", "condition": "obstruction_carpet"}, {"text": "Sealed natural floor.", "condition": "obstruction_sealed_floor"}]}], "content_source": "mixed", "photo_categories": ["woodworm_evidence", "room_overview"], "boilerplate_variants": [{"text": "Our surveyor did not identify any damage related to wood boring insects.", "condition": "no_damage"}, {"text": "Our inspection confirmed an historic attack of common furniture beetle (anobium punctatum).", "condition": "historic_attack"}, {"text": "Our inspection confirmed an active infestation of common furniture beetle (anobium punctatum - picture below).", "condition": "active_infestation"}, {"text": "The infestation was noted to the following areas:", "condition": "infestation_areas_intro"}]}, {"key": "proposals", "type": "proposals", "order": 6, "title": "Proposal", "required": true, "data_fields": ["spray_floor_area", "spray_timber_area"], "content_source": "mixed", "boilerplate_variants": [{"text": "No further treatment is necessary.", "condition": "no_treatment"}, {"text": "We would propose for the following works to be carried out:", "condition": "treatment_header"}, {"text": "Create discrete openings as required to facilitate treatment.", "condition": "proposal_discrete_openings"}, {"text": "Carry out treatment for woodboring insects using our environmentally friendly (water based with no solvents or propellants) micro biocidal fluid fogging system.", "condition": "proposal_fogging"}, {"text": "Create access points to facilitate treatment of the staircase.", "condition": "proposal_staircase"}, {"text": "Roof void", "condition": "proposal_roof_void_heading"}, {"text": "Remove loft insulation and set aside to permit access to the roof base timbers.", "condition": "proposal_remove_insulation"}, {"text": "Carry out treatment for the eradication of woodworm infestation.", "condition": "proposal_roof_treatment"}, {"text": "Treat all roof timbers and ceiling studs to the loft area.", "condition": "proposal_treat_roof_timbers"}, {"text": "Replace the loft insulation quilt.", "condition": "proposal_replace_insulation"}, {"text": "Replace structurally compromised timbers to:", "condition": "proposal_replace_timbers"}]}, {"key": "treatment_methodology", "type": "treatment", "order": 7, "title": "Treatment", "required": false, "content_source": "template", "boilerplate_variants": [{"text": "We will apply new generation, Microtec woodworm treatment chemicals using our specialist Electronic aerosol micro spray system. This system allows the work to be carried out with minimum disruption. Treatment by electro-aerosol allows the chemical to permeate all connected voids with extremely high surface coverage even to remote areas.", "condition": "treatment_method"}, {"text": "Loft Insulation (where possible) will be lifted to one side of the roof void area to expose timbers below, that area will be treated. The insulation will then be transferred to the treated side, the now exposed area will be treated and the insulation will be re-laid flat.", "condition": "insulation_handling"}]}, {"key": "exclusion_zone", "type": "boilerplate", "order": 8, "title": "Exclusion Zone", "required": false, "content_source": "template", "boilerplate_variants": [{"text": "There will be no permitted entry to the areas of treatment, during the treatment and for 1 hour after completion of treatment works. Exclusion will be to the whole of all rooms treated and if communal areas such as halls, landings are treated we advise vacating the whole property. This applies to humans and all animals/pets.", "condition": "always"}, {"text": "Fish tanks should be covered and sealed for the same duration. We advise that fish are removed and kept away from the area and that the tank water is changed prior to return of the fish.", "condition": "fish_tanks"}]}, {"key": "ancillary_items", "type": "closing", "order": 9, "title": "Ancillary Items", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "No allowance has been made in our estimate for the removal or replacement of floor coverings, furnishings, furniture, roof void stored contents and any other items that may obstruct our work. Whilst we will be happy to roll back and loosely relay carpets, you should arrange for rooms to be cleared of anything delicate or of value, prior to our arrival on site. Unless otherwise agreed in writing, any time spent clearing areas to enable work to proceed will be charged for at our standard hourly day rates and added to your final account.", "condition": "always"}]}, {"key": "extent_of_survey", "type": "closing", "order": 10, "title": "Extent of Survey", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "The areas we have reported upon are those inspected in accordance with your instructions. If there are any omissions or if you believe that we have misinterpreted your survey instruction, please let us know at once. Where treatment has been recommended, unless otherwise stated above, this is on the understanding that the specified area has not previously been treated and guaranteed.\n\nYou should be aware that we have reported upon problems evident to us at the time of our inspection. We are not commenting in any general sense on the risks of dampness, fungal decay or any other defect not evident at the time of inspection, or that may develop in the future.", "condition": "always"}]}, {"key": "payment_terms", "type": "closing", "order": 11, "title": "Payment", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "An initial payment of 30% of the contract value will be payable in advance, this will give us confidence to order job specific materials, and to book the time for your project into our technicians schedules. The balance should always be paid in full within 7 days of completion.", "condition": "always"}]}, {"key": "surveyor_comments", "type": "findings", "order": 12, "title": "Additional Supporting Comments From Surveyor", "required": false, "content_source": "llm_generated", "llm_prompt_hint": "Provide practical advice about the woodworm findings. If no guarantee or recorded treatment exists, recommend treatment. Mention the guarantee period. Keep tone professional."}, {"key": "surveyor_signature", "type": "signature", "order": 13, "title": "Report Author", "required": true, "data_fields": ["surveyor_name", "surveyor_credentials", "surveyor_title"], "content_source": "survey_data", "boilerplate_variants": [{"text": "Report produced under the guidance of Tyne Tees Damp Proofing Ltd by:", "condition": "always"}]}]', '{"company_name": "Tyne Tees Damp Proofing Ltd", "title_prefix": "Specific Defects Inspection - Woodworm", "abbreviations": {}, "company_website": "https://www.tyneteesdampproofing.co.uk", "guarantee_years": 25, "include_sketch_plan": false, "include_payment_terms": true, "general_notes_document": "General Notes for clients and Health and Safety precautions", "payment_deposit_percentage": 30}', true, '2026-02-20 02:08:46.553006+00', '2026-02-20 02:08:46.553006+00');
INSERT INTO public.report_templates VALUES ('bffd45dc-7b08-4d5d-bfe8-980f9d034358', 'Damp Survey Report v2', 'damp', 2, '[{"key": "cover", "type": "cover", "order": 1, "title": "Survey Report", "required": true, "data_fields": ["client_name", "site_address", "site_city", "site_county", "site_postcode", "reference", "inspection_date", "weather_conditions"], "content_source": "survey_data"}, {"key": "about_us", "type": "boilerplate", "order": 2, "title": "About Tyne Tees Damp Proofing Ltd", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "Tyne Tees Damp Proofing Ltd is a specialist remedial contractor serving residential and commercial clients across the North East of England, including Tyneside, Wearside, Northumberland and County Durham. Our team has been working in the damp proofing industry since 1987.\n\nWe specialise in the diagnosis and treatment of rising damp, penetrating damp, condensation, timber decay (dry rot and wet rot), woodworm infestation and basement waterproofing. Our surveyors are trained to correctly differentiate between the causes of damp — ensuring accurate diagnosis and effective treatment first time.\n\nQualifications & Standards\n\nOur surveyors hold the Certificated Surveyor in Remedial Treatments (CSRT) qualification and are Associate members of the Institute of Specialist Surveyors and Engineers (A.Inst.SSE). All survey and treatment works are carried out using only BBA-approved products and in accordance with current industry best practice.\n\nGuarantees\n\nWe offer 25-year company and insurance-backed guarantees for damp proofing and timber treatment works above external ground level. All guarantees cover both materials and labour. Our membrane products carry a 25-year manufacturer''s product guarantee. Insurance-backed guarantees are issued through the Westminster Protected Guarantee scheme, which operates independently of the contractor and does not rely on renewal premiums for continued cover — providing genuine long-term protection that is fully transferable to future property owners.\n\nOur Track Record\n\nWe maintain a 100% success rate on all damp and timber treatments, with no guarantee claims to date.", "condition": "always"}]}, {"key": "survey_context", "type": "boilerplate", "order": 3, "title": "The Survey", "required": true, "data_fields": ["reported_defect"], "content_source": "mixed", "boilerplate_variants": [{"text": "In accordance with your instructions, we carried out a specific defects inspection of the above property for the following reported problem:\n\nReported defect: {reported_defect}\n\nOrientation\n\nThe terms left, right, front and rear are used as viewed from the outside of the building, facing the front elevation.\n\nScope of Inspection\n\nThis inspection was limited to identifying evidence of the problems listed above within the areas indicated to us at the time of our visit. Any areas not referred to in this report were not inspected and are not covered by our findings or estimate.\n\nWhere treatment has been recommended, this is on the understanding that the specified area has not previously been treated under an existing guarantee, unless otherwise stated.\n\nAbbreviations\n\nDPC — Damp Proof Course · DPM — Damp Proof Membrane · USCC — Under Separate Contract and Costs · ACMs — Asbestos Containing Materials · W/W — Water Weight", "condition": "always"}]}, {"key": "property", "type": "property", "order": 4, "title": "The Property", "required": true, "data_fields": ["property_type", "construction", "build_year"], "content_source": "survey_data", "photo_categories": ["street_view", "property_front"]}, {"key": "external_inspection", "type": "findings", "order": 5, "title": "External Inspection", "required": true, "data_fields": ["building_defects", "wall_tie_concern", "cracking_concern", "ground_level_issues"], "sub_sections": [{"key": "building_defects", "type": "findings", "order": 1, "title": "Building Defects", "required": true, "data_fields": ["building_defects_found", "building_defects"], "content_source": "mixed", "photo_categories": ["building_defect", "roof_defect", "gutter_defect", "pointing_defect"], "boilerplate_variants": [{"text": "No building defects were noted at the time of our inspection.", "condition": "no_defects"}, {"text": "We noted the following building defects:", "condition": "defects_found"}, {"text": "The above items should be checked and addressed in the short term (3 months maximum) as they have been identified as possible causes of moisture ingress to the fabric of the building, failure to correct them may exacerbate the reported problems.", "condition": "defects_warning_cause"}, {"text": "The above items should be checked and addressed in the short term (3 months maximum) as they have been identified as possible causes of moisture ingress to the fabric of the building.", "condition": "defects_warning_general"}, {"text": "You should check your lease and find who is responsible for the upkeep of the roof, it is possible the upstairs flat may need to carry out the repairs or it could be shared cost. Whilst the roof repairs may fall under the freeholder''s responsibility, any damp or timber decay resulting from the roof failure will be your responsibility to remedy.", "condition": "flat_lease_warning"}]}, {"key": "wall_ties", "type": "findings", "order": 2, "title": "Wall Ties", "required": false, "data_fields": ["wall_tie_concern"], "content_source": "mixed", "boilerplate_variants": [{"text": "Our surveyor noted lateral cracks within the render/mortar beds, this is often a sign of decayed wall ties.", "condition": "wall_tie_concern"}, {"text": "You should instruct our survey department to arrange a wall tie survey USCC.", "condition": "wall_tie_recommend_internal"}, {"text": "We would suggest you arrange a wall tie inspection from a reputable specialist under separate contract and costs.", "condition": "wall_tie_recommend_external"}]}, {"key": "cracking", "type": "findings", "order": 3, "title": "Cracking To Walls", "required": false, "data_fields": ["cracking_concern"], "content_source": "mixed", "boilerplate_variants": [{"text": "Our surveyor noted lateral/stepped cracks within the render/mortar beds, this is often a sign of movement within the structure of a building.", "condition": "cracking_concern"}, {"text": "You should instruct our survey department to arrange a structural repair survey USCC.", "condition": "cracking_recommend_survey"}, {"text": "The cracking is quite pronounced and we recommend you advise for a structural engineer to assess the integrity of the building, under separate contract and costs.", "condition": "cracking_recommend_engineer"}]}, {"key": "ground_levels", "type": "findings", "order": 4, "title": "External Ground Levels", "required": false, "content_source": "mixed", "boilerplate_variants": [{"text": "There were no apparent ground level issues.", "condition": "no_ground_issues"}, {"text": "We noted high ground levels to the following areas:", "condition": "high_ground_levels"}, {"text": "External ground was falling towards building at:", "condition": "ground_falling_towards"}, {"text": "All ground levels to be maintained at a minimum of 150mm below DPC level, paths, drives, yards etc should not cause rainwater etc to run against your property walls. You should arrange work to correct this as a matter of urgency. Ideally, ground levels should be sloping away from the building to shed rainwater and to allow the building to dry out.", "condition": "ground_recommendation"}, {"text": "The following areas would benefit from the installation of an aco drain channel to carry water to the drain.", "condition": "aco_drain_recommended"}, {"text": "Lower the ground levels to the affected area of the property to create sufficient clearance below the DPC.", "condition": "lower_ground_levels"}, {"text": "Form a french drain by installing gravel to promote breathing of the walls to reduce damp levels.", "condition": "french_drain_recommended"}]}], "content_source": "mixed", "photo_categories": ["building_defect", "general"]}, {"key": "dpc_findings", "type": "findings", "order": 6, "title": "Damp Proof Course", "required": true, "data_fields": ["dpc_type", "dpc_condition", "dpc_evidence"], "content_source": "mixed", "boilerplate_variants": [{"text": "There is evidence of an original damp proof course in the mortar bed of the property.", "condition": "dpc_original_exists"}, {"text": "There is evidence of a chemical injection damp proof course to the property.", "condition": "dpc_chemical_exists"}, {"text": "The area where the damp proof course should be located, was not visible and we can neither confirm or deny the existence of a damp proof course installation.", "condition": "dpc_not_visible"}, {"text": "It is possible that work may have been carried out by another company in the past.", "condition": "dpc_previous_work"}, {"text": "You should check if any guarantee exists and if the work now required is covered by that guarantee as any work carried out by ourselves may invalidate existing guarantees.", "condition": "dpc_check_guarantee"}, {"text": "Install a low pressure injection damp proof course system, into the mortar courses of the walls as indicated on the attached sketch plan.", "condition": "dpc_proposal"}]}, {"key": "sub_floor_ventilation", "type": "findings", "order": 7, "title": "Sub Floor Ventilation", "required": false, "data_fields": ["airbrick_clean_count", "airbrick_upgrade_count", "airbrick_new_count"], "content_source": "mixed", "boilerplate_variants": [{"text": "There were no airbricks installed.", "condition": "no_airbricks"}, {"text": "It was not possible to inspect the airbricks due to the following:", "condition": "airbricks_not_inspectable"}, {"text": "The sub floor voids were ventilated by the following ''visible'' number of airbricks:", "condition": "airbricks_present"}, {"text": "It is our opinion that the above airbricks are providing sufficient airflow to the sub floor voids as the above reading is below 20% W/W.", "condition": "ventilation_sufficient"}, {"text": "It is our opinion that the above airbricks do not provide sufficient airflow to the sub floor voids as this moisture reading is equal to or above 20% W/W.", "condition": "ventilation_insufficient"}, {"text": "During our inspection it was noted that some of the existing airbricks appeared to be blocked and may be providing insufficient airflow to the sub floor voids.", "condition": "airbricks_blocked"}, {"text": "During our inspection it was noted that some of the existing airbricks were installed too low to the external ground levels and may permit water ingress to the sub floor voids.", "condition": "airbricks_too_low"}, {"text": "Remove air bricks, check airflow, adjust as necessary, clean debris & reinstall.", "condition": "proposal_remove_clean"}, {"text": "Upgrade air bricks to 225 x 150 airbricks.", "condition": "proposal_upgrade"}, {"text": "Install new 225 x 150 additional airbricks.", "condition": "proposal_install_new"}, {"text": "This will increase the airflow through the floor voids, reduce the humidity and the moisture content of linked timbers, which will greatly reduce the chances of attack by wood rotting fungi such as dry rot or wet rot.", "condition": "proposal_benefit"}, {"text": "Lift the existing air bricks.", "condition": "proposal_lift"}, {"text": "Lifting the airbricks will help to prevent water ponding and snow melting, causing water to run into the sub floor void, elevating the moisture levels of the timbers within.", "condition": "lift_benefit"}, {"text": "No further works required.", "condition": "no_works_required"}]}, {"key": "room_findings", "type": "room_findings", "order": 8, "title": "Internal Inspection", "required": true, "repeats_per": "room", "content_source": "llm_generated", "llm_prompt_hint": "Write a 1-2 paragraph narrative for this room describing the damp findings. State what was found, where it was found, and what treatment is recommended. Reference specific measurements. End with the recommended treatment action. Note: This was a non-destructive inspection. All findings are based on visual assessment, electronic moisture meter readings, digital laser thermometer readings and tactile examination of accessible surfaces. No opening up works were carried out.", "photo_categories": ["damp_evidence", "room_general"]}, {"key": "summary_of_works", "type": "data", "order": 9, "title": "Summary of Proposed Works", "required": true, "data_fields": ["rooms_summary", "additional_works"], "content_source": "survey_data"}, {"key": "surveyor_comments", "type": "findings", "order": 10, "title": "Surveyor Comments", "required": false, "content_source": "llm_generated", "llm_prompt_hint": "Write 2-3 paragraphs summarising the overall survey findings. Reference specific rooms, measurements and treatments. State the most urgent priorities. Provide practical context for the homeowner about the recommended works."}, {"key": "ancillary_items", "type": "boilerplate", "order": 11, "title": "Ancillary Items", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "Our estimate does not include the removal or replacement of floor coverings, furnishings, furniture, stored items in roof voids, or any other items that may obstruct the works.\n\nPlease arrange for rooms to be cleared of anything delicate or of value before our arrival on site. We are happy to roll back and loosely relay carpets where possible.\n\nAny time spent clearing areas to enable work to proceed will be charged at our standard day rates and added to your final account, unless otherwise agreed in writing.", "condition": "always"}]}, {"key": "extent_of_survey", "type": "boilerplate", "order": 12, "title": "Extent of Survey", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "We have reported on the areas inspected in accordance with your instructions. If you believe any areas have been omitted or that we have misinterpreted your requirements, please contact us immediately.\n\nOur findings relate to conditions evident at the time of inspection. We are not commenting on the general risk of dampness, fungal decay or any other defect not visible during our visit, or that may develop in the future.", "condition": "always"}]}, {"key": "payment_terms", "type": "boilerplate", "order": 13, "title": "Payment Terms", "required": true, "content_source": "template", "boilerplate_variants": [{"text": "An initial payment of 30% of the contract value is required before works commence. This covers the cost of ordering materials specific to your project and securing a date in our installation schedule.\n\nThe remaining balance is due within 7 days of completion.", "condition": "always"}]}, {"key": "surveyor_profile", "type": "signature", "order": 14, "title": "Your Surveyor", "required": true, "data_fields": ["surveyor_name", "surveyor_title", "surveyor_credentials", "surveyor_photo", "surveyor_experience"], "content_source": "survey_data"}, {"key": "sketch_plan", "type": "sketch", "order": 15, "title": "Sketch Plan", "required": false, "content_source": "surveyor_input"}]', '{"company_name": "Tyne Tees Damp Proofing Ltd", "title_prefix": "Specific Defects Inspection - Rising Damp", "abbreviations": {"DPC": "Damp Proof Course", "DPM": "Damp proof membrane", "W/W": "Water Weight", "ACMs": "Asbestos containing materials", "USCC": "Under separate contract and costs"}, "company_website": "https://www.tyneteesdampproofing.co.uk", "guarantee_years": 25, "include_sketch_plan": true, "include_payment_terms": true, "general_notes_document": "General Notes for clients and Health and Safety precautions", "payment_deposit_percentage": 30}', true, '2026-02-20 02:08:46.536741+00', '2026-02-20 18:40:52.071123+00');
